import React from "react";

 function setItemInLocalStorage(tokenName, data) {

  localStorage.setItem(tokenName, JSON.stringify(data[0] ) );
  window.location = '/';
}

 function removeFromLocalStorage(tokenName) {
    localStorage.removeItem(tokenName);
    window.location = '/';
}

 function getItemFromLocalStorage(tokenName) {
    localStorage.getItem(tokenName);

}
