const express = require('express');
const mysql = require('mysql');
const api = express();
api.use(express.json()); 

let connecntion = mysql.createConnection({
    host : "localhost",
    user : "root",
    password: "tiger",
    database: "library"
});
connecntion.connect( (err) =>{
    if(err)
     throw err;
     console.log("connected");
} );

const getSongs = () => {
    const select = 'select title from songs';
     connecntion.query(select, (err, result) => {
        console.log(result)
        return result;
    } );
}
 
export default getSongs;
