import React, { Component } from "react";
import axios from 'axios';
class HomePage extends Component {
  state = {
    songs: []
  };
  async componentDidMount() {
    const {data : songs} = await axios.get('http://localhost:5000/music/songs');
    console.log(songs);
    this.setState({ songs });
  }
  handleAddSong = () =>{
    this.props.history.push('/addNewSong');
  }
  
  render() {
    return (

      <React.Fragment>

      <button onClick = {this.handleAddSong} className = "btn btn-danger m-1" >Add Song</button>  
      <table className="table table-sm m-3">
        <thead>
          <tr>
            <th scope="col">Song's number </th>
            <th scope="col">Song's name </th>
            <th scope="col">Artist's name</th>
            <th scope ="col"></th>
          </tr>
        </thead>
        <tbody>
          {this.state.songs.map( (col) => (
            <tr key = {col.id} >
              <th scope="row"> {col.id} </th>
              <td>{col.title}</td>
              <td> {col.name} </td>
              
            </tr>
          ))}
        </tbody>
      </table>
      </React.Fragment>
    );
  }
}

export default HomePage;
