import React from "react";
import Form from "./common/form";
import Axios from "axios";
import { Route, withRouter } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// import  Joi  from 'joi-browser';

class LogInForm extends Form {
  state = {
    data: { username: "", password: "" },
    errors: {},
  };
  // schema = {
  //     username : Joi.string().required().label('Username'),
  //     password : Joi.string().required().label('Password')
  // }

  handleLogin = async (event) => {
    event.preventDefault();
    const userEmail = this.state.data.username;
    const userPassword = this.state.data.password;
    const { data } = await Axios.get(
      `http://localhost:5000/searchForUsers/'${userEmail}'/'${userPassword}' `
    );
    console.log(data.length);
    if (data.length > 0) {
      this.props.history.push("/"); // redirect to HomePage
    } else {
      // Doesn't work
      //toast("Incorrect Username or Password");
      
      <div
        class="toast align-items-center text-white bg-primary border-0"
        role="alert"
        aria-live="assertive"
        aria-atomic="true"
      >
        <div class="d-flex">
          <div class="toast-body">Incorrect Username or Password</div>
          <button
            type="button"
            class="btn-close btn-close-white me-2 m-auto"
            data-bs-dismiss="toast"
            aria-label="Close"
          ></button>
        </div>
      </div>;
    }
  };
  render() {
    return (
      <div>
        <h1>Login Form</h1>
        <form>
          {this.renderInput("username", "Username", true)}
          {this.renderInput("password", "Password", false, "password")}
          {this.renderCheckBox()}
          <button onClick={this.handleLogin} className="btn btn-primary">
            {" "}
            Login{" "}
          </button>
        </form>
        <a className="link" href="/register">
          Doesn't have an account yet!
        </a>
      </div>
    );
  }
}

export default LogInForm;
