import React from "react";

export function setItem(tokenName, data) {

  localStorage.setItem(tokenName, JSON.stringify(data[0] ) );
  window.location = '/';
}

export function removeFromLocalStorage(tokenName) {
    localStorage.removeItem(tokenName);
    window.location = '/';
}
