import React, { Component } from 'react';

const mysql = require('mysql');

let connecntion = mysql.createConnection({
    host : "localhost",
    user : "root",
    password: "tiger",
    database: "library"
});
connecntion.connect( (err) =>{} )

const getSongs = () => {
    const select = 'select title from songs';
     connecntion.query(select, (err, result) => {
        console.log(result[1]);
    } );
   
}
 
export default getSongs;
